import React, { useState } from 'react';
import style from './hamburgerIcon.module.scss';
import classNames from 'classnames';

export const HamburgerIcon: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);

  const circleStyles = {
    [`${style.circle_opened}`]: isOpen
  };
  return (
    <div className='hamburger_Icon'>
      <input
        type="checkbox"
        id="toggle-hamburger"
        className={`${style.checkbox_input}`}
        onClick={() => setIsOpen(!isOpen)}
      />
      <label htmlFor="toggle-hamburger"
        className={`${style.toggle_label}`}
        id="toggle-label"
      >
        <svg
          className={classNames(
            `${style.toggle_label_circle}`,
            circleStyles
          )}
          width="36px"
          height="36px"
        >
          <circle
            r="17"
            cx="18"
            cy="18"
          ></circle>
        </svg>
        <div className={`${style.toggle_label_hamburger}`}>
          <div className={`${style.center}`}></div>
        </div>
      </label>
    </div>
  );
};
